// This a component that will take toggleLike (find toggleLike function/snippet), delay as props and the update toggle the toggleLike function 
export default function DoubleTap(props) {
    const defaultProps = {
      delay: 300,
      //onDoubleTap is the passed function called toggleLike
      onDoubleTap: () => null,
    };

    let lastTap = null;
    const handleDoubleTap = () => {
      const now = Date.now();
      if (lastTap && now - lastTap < (props.delay || defaultProps.delay)) {
        props.onDoubleTap() || defaultProps.onDoubleTap();
      } else {
        lastTap = now;
      }
    };

    return (
      <TouchableWithoutFeedback onPress={handleDoubleTap}>
